import React, {useState, useContext} from 'react'
import {Link} from 'react-router-dom'
import {GlobalState} from '../../GlobalState'
import axios from 'axios'
import { FiShoppingCart, FiAlignRight} from "react-icons/fi";
function Header() {
    const state = useContext(GlobalState)
    const [isLogged] = state.userAPI.isLogged
    const [isAdmin] = state.userAPI.isAdmins

    const logoutUser = async () =>{
        await axios.get('/api/user/logout')
        
        localStorage.removeItem('firstLogin')
        
        window.location.href = "/";
    }
    const adminRouter = () =>{
        return(
            <>
                <li><Link to="/create_product">Create Product</Link></li>
                <li><Link to="/category">Categories</Link></li>
            </>
        )
    }

    const loggedRouter = () =>{
        return(
            <>
                <li><Link to="/history">History</Link></li>
                <li><Link to="/" onClick={logoutUser}>Logout</Link></li>
            </>
        )
    }
    return (
        <header className="flex justify-between items-center h-18 w-full m-auto py-4 shadow px-8 bg-gray-50">
            <div className="md:hidden">
                <FiAlignRight className=" mr-5 text-4xl md:hidden" />
            </div>

            <div>
                <h1 className="text-3xl font-bold">
                    <Link to="/"><span className="text-red-500">{isAdmin ? 'Admin' : 'LOGO'}</span></Link>
                </h1>
            </div>

            <div className="hidden space-x-8 md:flex md:items-center md:justify-center">
                <span className="text-md uppercase"><Link to="/">{isAdmin ? 'Products' : 'Shop'}</Link></span>
                {isAdmin && adminRouter()}
                    {
                        isLogged ? loggedRouter() : <li><Link to="/login">Login ✥ Register</Link></li>
                    }
                <span className="text-md uppercase"><Link to="/login">Login</Link></span>
                <Link to="/cart"><FiShoppingCart className="ml-6 text-2xl"/></Link>
                <span className="absolute text-xs flex justify-center items-center w-4 h-4 top-4 right-6 bg-red-400 rounded-full">0</span>
                <span className="absolute text-xs flex justify-center items-center w-4 h-4 top-4 right-6 bg-red-400 rounded-full animate-ping"></span>
            </div>
            {
                isAdmin ? '' 
                :<div className="cart-icon">
                    <span>{cart.length}</span>
                    <Link to="/cart">
                        <img src={Cart} alt="" width="30" />
                    </Link>
                </div>
            }

        </header>
    )
}

export default Header
