import React, {useContext, useState, useEffect} from 'react'
import { useParams, Link } from 'react-router-dom';
import { GlobalState } from "../../../GlobalState";

function DetailProducts() {
    const params = useParams()
    const state = useContext(GlobalState)
    const [products] = state.productsApi.products
    const [detailProduct, setDetailProduct] = useState([])

    useEffect(() =>{
        if(params.id){

            products.forEach(product => {
                if(product._id === params.id) setDetailProduct(product)
            })
        }
    },[params.id, products])

    if(detailProduct.length === 0) return null;
    return (
        <>
            <div className="flex flex-col w-3/4 mt-2 m-auto items-center justify-center bg-white md:grid md:grid-cols-2 md:w-3/4 md:h-96">
                <img className="md:m-auto md:h-full md:object-cover" src={detailProduct.images.url} alt="" />
                <div className="box-detail">
                    <div className="row">
                        <h2>{detailProduct.title}</h2>
                        <h6>#id: {detailProduct.product_id}</h6>
                    </div>
                    <span>$ {detailProduct.price}</span>
                    <p>{detailProduct.description}</p>
                    <p>{detailProduct.content}</p>
                    <p>Sold: {detailProduct.sold}</p>
                    {/* <Link to="/cart" className="cart"
                    onClick={() => addCart(detailProduct)}>
                        Buy Now
                    </Link> */}
                </div>
            </div>
        </>
    )
}

export default DetailProducts
