import React, { useContext} from 'react'
import { GlobalState } from "../../../GlobalState";
import { Link  } from "react-router-dom";
function Cart() {
    const state = useContext(GlobalState)
    const [cart] = state.userAPI.cart
    
    return (
        <div>
        {
            cart.map(product => (
                <div className=" border-r-8 border-red-300 transition duration-300 ease-in flex flex-col w-4/5 mt-2 m-auto items-center justify-center bg-white md:grid md:grid-cols-2 md:w-3/4 md:h-64">
                    <img className="md:m-auto md:object-cover md:h-64 md:w-full" src={product.images.url} alt="" />
                    <div className="box-detail m-0 bg-gray-100 w-full pl-5 pb-4 md:h-full">
                        <div className="row mb-3 mt-3">
                            <h2 className="text-xl md:text-3xl capitalize font-bold">{product.title}</h2>
                            <h6 className="text-sm font-semibold">#pr_id: {product.product_id}</h6>
                        </div>
                        <span className="text-xs">$ {product.price}</span>
                        <p className="text-xs">{product.description}</p>
                        <p className="text-xs">{product.content}</p>
                        <p className="text-xs mb-3">Sold: {product.sold}</p>
                        <Link to="/cart" className="bg-gray-800 text-white rounded-full px-3 py-2 uppercase text-xs ">
                            Buy Now
                        </Link>
                    </div>
                </div>
            ))
        }


        </div>
    )
}

export default Cart
